import React from 'react';

import type { CarePathIcons } from './care-path-icon.types';

export interface CarePathIconProps extends React.SVGAttributes<SVGElement> {
    /**
     * Icon color
     * @default currentColor
     * */
    color?: string;
    /**
     *  The icon to display. Conforms to `enum EpisodeCategory`.
     * */
    icon?: CarePathIcons;
    /**
     * HTML ID of the component
     * */
    id?: string;
    /**
     * Icon size in `px`
     * @default variant small 18, variant large 90
     * */
    size?: number | string;
    /**
     * Icon variant
     * @default large-multicolor
     */
    variant?: 'small' | 'large' | 'large-multicolor';
}

export function CarePathIcon({
    id,
    size,
    color = 'currentColor',
    variant = 'large-multicolor',
    icon = 'general',
    ...props
}: CarePathIconProps) {
    const primaryColor = '#FF9A1F';
    const secondaryColor = '#FF37F0';

    if (variant === 'small') {
        size = size ? size : 18;
        return (
            <svg viewBox="0 0 18 18" width={size} height={size} id={id} {...props}>
                {getSmallIcon(icon, color)}
            </svg>
        );
    }
    if (variant === 'large') {
        size = size ? size : 90;
        return (
            <svg
                xmlns="http://www.w3.org/2000/svg"
                viewBox="0 0 90 90"
                width={size}
                height={size}
                id={id}
                {...props}
            >
                {getLargeIcon(icon, color, color)}
            </svg>
        );
    }
    if (variant === 'large-multicolor') {
        size = size ? size : 90;
        return (
            <svg
                xmlns="http://www.w3.org/2000/svg"
                viewBox="0 0 90 90"
                width={size}
                height={size}
                id={id}
                {...props}
            >
                {getLargeIcon(icon, primaryColor, secondaryColor)}
            </svg>
        );
    }
    return null;
}

